template:
app.directive('aria', {
  mounted(el, binding) {
    useAriaDirectory(el, binding.value);
  },
  updated(el, binding) {
    useAriaDirectory(el, binding.value);
  },
});

// @/utils/useAria
export function useAriaDirectory(el, value) {
  const aria = getAriaAttributes(value);
  const keys = Object.keys(aria);

  keys.forEach((key) => {
    if (aria[key] === undefined) return;
    el.setAttribute(key, aria[key]);
  });
}
